import type { NextFunction, Request, Response } from "express";
import type { ParamsDictionary } from "express-serve-static-core";

import type { CreateBikeReqBody, GetBikesReqQuery, UpdateBikeReqBody } from "~/models/requests/bikes.request";
import type { TokenPayLoad } from "~/models/requests/users.requests";

import { BikeStatus, RentalStatus, Role } from "~/constants/enums";
import HTTP_STATUS from "~/constants/http-status";
import { BIKES_MESSAGES, USERS_MESSAGES } from "~/constants/messages";
import bikesService from "~/services/bikes.services";
import { ErrorWithStatus } from "~/models/errors";
import databaseService from "~/services/database.services";
import { ObjectId } from "mongodb";

export async function createBikeController(req: Request<ParamsDictionary, any, CreateBikeReqBody>, res: Response) {
  const result = await bikesService.createBike(req.body);
  return res.status(HTTP_STATUS.CREATED).json({
    message: BIKES_MESSAGES.CREATE_BIKE_SUCCESS,
    result,
  });
}

export async function getBikesController(
  req: Request<ParamsDictionary, any, any, GetBikesReqQuery>,
  res: Response,
  next: NextFunction,
) {
  const { user_id } = req.decoded_authorization as TokenPayLoad
  const user = await databaseService.users.findOne({ _id: new ObjectId(user_id) });
  if(!user){
    throw new ErrorWithStatus({
      message: USERS_MESSAGES.USER_NOT_FOUND,
      status: HTTP_STATUS.NOT_FOUND
    })
  }

  const { role } = user
  const query = req.query;

  // Nếu là user, chỉ cho phép xem xe có sẵn (AVAILABLE)
  if (role === Role.User) {
    query.status = BikeStatus.Available;
  }
  await bikesService.getAllBikes(res, next, query);
}

export async function getBikeByIdController(req: Request, res: Response) {
  const { _id } = req.params;
  const result = await bikesService.getBikeById(_id);
  return res.json({
    message: BIKES_MESSAGES.GET_BIKE_SUCCESS,
    result,
  });
}

export async function reportBrokenBikeController(req: Request, res: Response) {
  const { _id: bikeId } = req.params
  const { user_id } = req.decoded_authorization as TokenPayLoad

  // Kiểm tra xem user có đang thuê xe này không
  const rental = await databaseService.rentals.findOne({
    user_id: new ObjectId(user_id),
    bike_id: new ObjectId(bikeId),
    status: RentalStatus.Rented
  })

  if (!rental) {
    throw new ErrorWithStatus({
      message: BIKES_MESSAGES.CANNOT_REPORT_BIKE_NOT_RENTING,
      status: HTTP_STATUS.FORBIDDEN
    })
  }

  const result = await bikesService.updateBike(bikeId, { status: BikeStatus.Broken })

  return res.json({
    message: BIKES_MESSAGES.REPORT_BROKEN_BIKE_SUCCESS,
    result
  })
}

// admin/staff cập nhật bike
export async function adminUpdateBikeController(req: Request<ParamsDictionary, any, UpdateBikeReqBody>, res: Response) {
  const { _id: bikeId } = req.params
  const payload = req.body

  const result = await bikesService.updateBike(bikeId, payload)

  return res.json({
    message: BIKES_MESSAGES.UPDATE_BIKE_SUCCESS,
    result
  })
}

export async function deleteBikeController(req: Request, res: Response) {
  const { _id: bikeId } = req.params;
  const result = await bikesService.deleteBike(bikeId);
  return res.json({
    message: BIKES_MESSAGES.DELETE_BIKE_SUCCESS,
    result,
  });
}

export async function getRentalsByBikeIdController(
  req: Request<ParamsDictionary, any, any, GetBikesReqQuery>,
  res: Response,
  next: NextFunction
) {
  const { _id: bikeId } = req.params;
  await bikesService.getRentalsByBikeId(res, next, bikeId, req.query);
}

export async function getBikesStatsController(req: Request, res: Response) {
  const result = await bikesService.getBikesStats();
  return res.json({
    message: BIKES_MESSAGES.GET_BIKE_STATS_SUCCESS,
    result,
  });
}

export async function getBikeStatsByIdController(req: Request, res: Response) {
  const { _id: bikeId } = req.params;
  const result = await bikesService.getBikeStatsById(bikeId);
  return res.json({
    message: BIKES_MESSAGES.GET_BIKE_STATS_SUCCESS,
    result,
  });
}

import type { Request, Response } from 'express'
import type { NextFunction, ParamsDictionary } from 'express-serve-static-core'
import type { Filter, ObjectId } from 'mongodb'

import { GroupByOptions, RentalStatus } from '~/constants/enums'
import type {
  CancelRentalReqBody,
  CreateRentalReqBody,
  EndRentalByAdminOrStaffReqBody,
  RentalParams,
  UpdateRentalReqBody
} from '~/models/requests/rentals.requests'
import type Bike from '~/models/schemas/bike.schema'
import type Rental from '~/models/schemas/rental.schema'
import type Station from '~/models/schemas/station.schema'

import { RENTALS_MESSAGE } from '~/constants/messages'
import databaseService from '~/services/database.services'
import rentalsService from '~/services/rentals.services'
import { sendPaginatedAggregationResponse, sendPaginatedResponse } from '~/utils/pagination.helper'
import { toObjectId } from '~/utils/string'
import { TokenPayLoad } from '~/models/requests/users.requests'

export async function createRentalSessionController(
  req: Request<ParamsDictionary, any, CreateRentalReqBody>,
  res: Response
) {
  const { user_id } = req.decoded_authorization as TokenPayLoad
  const station = req.station as Station
  const bike = req.bike as Bike

  const result = await rentalsService.createRentalSession({
    user_id,
    start_station: station._id as ObjectId,
    bike_id: bike._id as ObjectId
  })
  res.json({
    message: RENTALS_MESSAGE.CREATE_SESSION_SUCCESS,
    result
  })
}

export async function endRentalSessionController(req: Request<RentalParams>, res: Response) {
  const { user_id } = req.decoded_authorization as TokenPayLoad

  const rental = req.rental! as Rental

  const result = await rentalsService.endRentalSession({ user_id: toObjectId(user_id), rental })
  res.json({
    message: RENTALS_MESSAGE.END_SESSION_SUCCESS,
    result
  })
}

export async function endRentalByAdminOrStaffController(
  req: Request<RentalParams, any, EndRentalByAdminOrStaffReqBody>,
  res: Response
) {
  const { user_id } = req.decoded_authorization as TokenPayLoad

  const rental = req.rental! as Rental

  const result = await rentalsService.endRentalByAdminOrStaff({
    user_id: toObjectId(user_id),
    rental,
    payload: req.body
  })
  res.json({
    message: RENTALS_MESSAGE.END_SESSION_SUCCESS,
    result
  })
}

export async function getMyRentalsController(req: Request, res: Response, next: NextFunction) {
  const { user_id } = req.decoded_authorization as TokenPayLoad

  const filters: Filter<Rental> = {}
  filters.user_id = toObjectId(user_id)
  if (req.query.start_station) {
    filters.start_station = toObjectId(req.query.start_station.toString())
  }
  if (req.query.end_station) {
    filters.end_station = toObjectId(req.query.end_station.toString())
  }
  if (req.query.status) {
    filters.status = req.query.status as RentalStatus
  }
  await sendPaginatedResponse(res, next, databaseService.rentals, req.query, filters)
}

export async function getMyDetailRentalController(req: Request<RentalParams>, res: Response) {
  const { user_id } = req.decoded_authorization as TokenPayLoad

  const result = await rentalsService.getMyDetailRental({ user_id: toObjectId(user_id), rental_id: req.params.id })
  res.json({
    message: RENTALS_MESSAGE.GET_DETAIL_SUCCESS,
    result
  })
}

export async function getMyCurrentRentalsController(req: Request<RentalParams>, res: Response, next: NextFunction) {
  const { user_id } = req.decoded_authorization as TokenPayLoad
  const filter: Filter<Rental> = {
    user_id: toObjectId(user_id),
    status: RentalStatus.Rented
  }

  await sendPaginatedResponse(res, next, databaseService.rentals, req.query, filter)
}

// staff/admin only
export async function getAllRentalsController(req: Request, res: Response, next: NextFunction) {
  const filters: Filter<Rental> = {}
  if (req.query.start_station) {
    filters.start_station = toObjectId(req.query.start_station.toString())
  }
  if (req.query.end_station) {
    filters.end_station = toObjectId(req.query.end_station.toString())
  }
  if (req.query.status) {
    filters.status = req.query.status as RentalStatus
  }
  await sendPaginatedResponse(res, next, databaseService.rentals, req.query, filters)
}

export async function getDetailRentalController(req: Request<RentalParams>, res: Response) {
  const result = await rentalsService.getDetailRental({ rental_id: req.params.id })
  res.json({
    message: RENTALS_MESSAGE.GET_DETAIL_SUCCESS,
    result
  })
}

export async function updateDetailRentalController(
  req: Request<RentalParams, any, UpdateRentalReqBody>,
  res: Response
) {
  const { user_id } = req.decoded_authorization as TokenPayLoad
  const result = await rentalsService.updateDetailRental({
    rental_id: req.params.id,
    admin_id: user_id,
    payload: req.body
  })
  res.json({
    message: RENTALS_MESSAGE.UPDATE_DETAIL_SUCCESS,
    result
  })
}

export async function cancelRentalController(req: Request<RentalParams, any, CancelRentalReqBody>, res: Response) {
  const { user_id } = req.decoded_authorization as TokenPayLoad
  const result = await rentalsService.cancelRental({ rental_id: req.params.id, admin_id: user_id, payload: req.body })
  res.json({
    message: RENTALS_MESSAGE.CANCEL_RENTAL_SUCCESS,
    result
  })
}

export async function getRentalRevenueController(req: Request, res: Response) {
  const { from, to, groupBy } = req.query
  const result = await rentalsService.getRentalRevenue({
    from: from as string,
    to: to as string,
    groupBy: groupBy as GroupByOptions
  })
  res.json({
    message: RENTALS_MESSAGE.GET_REVENUE_SUCCESS,
    result
  })
}

export async function getStationActivityController(req: Request, res: Response) {
  const { from, to, stationId } = req.query
  const result = await rentalsService.getStationActivity({
    from: from as string,
    to: to as string,
    stationId: stationId as string
  })
  res.json({
    message: RENTALS_MESSAGE.GET_STATION_ACTIVITY_SUCCESS,
    result
  })
}

export async function getReservationsStatisticController(req: Request, res: Response) {
  const { from, to, groupBy } = req.query
  const result = await rentalsService.getReservationsStatistic({
    from: from as string,
    to: to as string,
    groupBy: groupBy as GroupByOptions
  })
  res.json({
    message: RENTALS_MESSAGE.GET_RESERVATIONS_STATISTIC_SUCCESS,
    result
  })
}

export async function getRentalsByStationIdController(req: Request, res: Response, next: NextFunction) {
  const { id: stationId } = req.params
  const { status, expired_within } = req.query
  const pipeline = await rentalsService.getRentalsByStationIdPipeline({
    stationId,
    status: status as RentalStatus,
    expired_within: (expired_within as string) ?? '60'
  })
  return sendPaginatedAggregationResponse(res, next, databaseService.rentals, req.query, pipeline)
}

import type { NextFunction, Request, Response } from 'express'
import type { ParamsDictionary } from 'express-serve-static-core'
import type { Filter } from 'mongodb'

import { ObjectId } from 'mongodb'

import type { ReportStatus, ReportTypeEnum } from '~/constants/enums'
import type { CreateReportReqBody } from '~/models/requests/reports.requests'
import type Report from '~/models/schemas/report.schema'

import HTTP_STATUS from '~/constants/http-status'
import { REPORTS_MESSAGES } from '~/constants/messages'
import { ErrorWithStatus } from '~/models/errors'
import databaseService from '~/services/database.services'
import reportService from '~/services/report.services'
import { sendPaginatedResponse } from '~/utils/pagination.helper'
import { TokenPayLoad } from '~/models/requests/users.requests'

export async function createReportController(req: Request<ParamsDictionary, any, CreateReportReqBody>, res: Response) {
  const { user_id } = req.decoded_authorization as TokenPayLoad

  const result = await reportService.createReport({
    userID: user_id,
    payload: req.body,
    files: req.files as Express.Multer.File[]
  })

  res.json({
    message: REPORTS_MESSAGES.CREATE_SUCCESS,
    result: { acknowledged: true, insertedId: result._id }
  })
}

export async function updateReportStatusController(req: Request<ParamsDictionary, any, any>, res: Response) {
  const { reportID } = req.params
  const newStatus = req.body.newStatus
  const assignee_id = req.body.staff_id || ''
  const priority = req.body.priority || ''

  const result = await reportService.updateReportStatus({
    reportID: reportID.toString(),
    newStatus,
    assignee_id,
    priority
  })

  res.json({
    message: REPORTS_MESSAGES.UPDATE_SUCCESS,
    result
  })
}

export async function getByIdController(req: Request<ParamsDictionary, any, any>, res: Response) {
  const reportID = req.params.reportID

  const result = await databaseService.reports.findOne({ _id: new ObjectId(reportID) })
  if (!result) {
    throw new ErrorWithStatus({
      message: REPORTS_MESSAGES.REPORT_NOT_FOUND.replace('%s', reportID),
      status: HTTP_STATUS.NOT_FOUND
    })
  }

  res.json({
    message: REPORTS_MESSAGES.GET_BY_ID_SUCCESS.replace('%s', reportID),
    result
  })
}

export async function getAllUserReportController(req: Request, res: Response, next: NextFunction) {
  try {
    const { user_id } = req.decoded_authorization as TokenPayLoad

    const filter = {
      user_id: new ObjectId(user_id),
      status: req.query.status as ReportStatus
    }

    await sendPaginatedResponse(res, next, databaseService.reports, req.query, filter, {})
  } catch (error) {
    next(error)
  }
}

export async function getAllReportController(req: Request<any, any, any>, res: Response, next: NextFunction) {
  const filter: Filter<Report> = {}
  if (req.query.type) {
    filter.type = req.query.type as ReportTypeEnum
  }

  if (req.query.userID) {
    filter.user_id = new ObjectId(req.query.userID as string)
  }

  if (req.query.date) {
    const start = new Date(req.query.date as string)
    const end = new Date(start)
    end.setUTCHours(23, 59, 59, 999)

    filter.created_at = { $gte: start, $lte: end }
  }

  await sendPaginatedResponse(res, next, databaseService.reports, req.query, filter)
}

import type { NextFunction, Request, Response } from 'express'
import type { ParamsDictionary } from 'express-serve-static-core'

import { ObjectId } from 'mongodb'

import { SupplierStatus } from '~/constants/enums'
import type {
  CreateSupplierReqBody,
  GetSupplierReqQuery,
  UpdateSupplierReqBody
} from '~/models/requests/suppliers.request'

import HTTP_STATUS from '~/constants/http-status'
import { SUPPLIER_MESSAGE } from '~/constants/messages'
import { ErrorWithStatus } from '~/models/errors'
import databaseService from '~/services/database.services'
import supplierService from '~/services/supplier.services'

export async function createSupplierController(req: Request<any, any, CreateSupplierReqBody>, res: Response) {
  const result = await supplierService.createSupplier({
    payload: req.body
  })

  res.json({
    message: SUPPLIER_MESSAGE.CREATE_SUCCESS,
    result
  })
}

export async function updateSupplierController(
  req: Request<ParamsDictionary, any, UpdateSupplierReqBody>,
  res: Response
) {
  const supplierID = req.params.id

  const result = await supplierService.updateSupplier({ id: supplierID.toString(), payload: req.body })

  res.json({
    message: SUPPLIER_MESSAGE.UPDATE_SUCCESS,
    result
  })
}

export async function changeSupplierStatusController(req: Request<ParamsDictionary, any, any>, res: Response) {
  const supplierID = req.params.id
  const { newStatus } = req.body

  const result = await supplierService.updateStatus({
    id: supplierID.toString(),
    newStatus: newStatus as SupplierStatus
  })

  res.json({
    message: SUPPLIER_MESSAGE.UPDATE_SUCCESS,
    result
  })
}

export async function getByIdController(req: Request<ParamsDictionary, any, any>, res: Response) {
  const supplierID = req.params.id

  const result = await databaseService.suppliers.findOne({ _id: new ObjectId(supplierID) })
  if (!result) {
    throw new ErrorWithStatus({
      message: SUPPLIER_MESSAGE.SUPPLIER_NOT_FOUND.replace('%s', supplierID),
      status: HTTP_STATUS.NOT_FOUND
    })
  }

  res.json({
    message: SUPPLIER_MESSAGE.GET_BY_ID_SUCCESS.replace('%s', supplierID),
    result
  })
}

export async function getAllSupplierStatController(req: Request<any, any, any>, res: Response) {
  const result = await supplierService.getAllSupplierBikeStats()

  res.json({
    message: SUPPLIER_MESSAGE.GET_STATS_SUCCESS,
    result
  })
}

export async function getSupplierStatController(req: Request<ParamsDictionary, any, any>, res: Response) {
  const supplierID = req.params.id
  const result = await supplierService.getSupplierBikeStats(supplierID)

  res.json({
    message: SUPPLIER_MESSAGE.GET_STATS_SUCCESS_BY_ID.replace('%s', supplierID),
    result
  })
}

export async function getAllSupplierController(
  req: Request<ParamsDictionary, any, any, GetSupplierReqQuery>,
  res: Response,
  next: NextFunction
) {
  const query = req.query

  await supplierService.getAllSupplier(res, next, query)
}

import type { NextFunction, Request, Response } from "express";
import type { ParamsDictionary } from "express-serve-static-core";

import { ObjectId } from "mongodb";

import type { ChangePasswordReqBody, LoginReqBody, LogoutReqBody, RefreshTokenReqBody, RegisterReqBody, resetPasswordReqBody, TokenPayLoad, VerifyEmailReqBody } from "~/models/requests/users.requests";
import type User from "~/models/schemas/user.schema";

import { UserVerifyStatus } from "~/constants/enums";
import HTTP_STATUS from "~/constants/http-status";
import { USERS_MESSAGES } from "~/constants/messages";
import { ErrorWithStatus } from "~/models/errors";
import databaseService from "~/services/database.services";
import usersService from "~/services/users.services";

export async function loginController(req: Request<ParamsDictionary, any, LoginReqBody>, res: Response) {
  const user = req.user as User;
  const user_id = user._id as ObjectId;

  const result = await usersService.login({
    user_id: user_id.toString(),
    verify: user.verify,
  });

  res.json({
    message: USERS_MESSAGES.LOGIN_SUCCESS,
    result,
  });
}

export async function registerController(req: Request<ParamsDictionary, any, RegisterReqBody>, res: Response) {
  const result = await usersService.register(req.body);
  res.json({
    message: USERS_MESSAGES.REGISTER_SUCCESS,
    result,
  });
}

export async function logoutController(req: Request<ParamsDictionary, any, LogoutReqBody>, res: Response) {
  const { refresh_token } = req.body;
  const result = await usersService.logout(refresh_token);
  res.json(result);
}

export async function forgotPasswordController(req: Request, res: Response) {
  const { _id, email, fullname, verify } = req.user as User;
  const result = await usersService.forgotPassword({
    user_id: (_id as ObjectId).toString(),
    email,
    fullname,
    verify,
  });
  res.json(result);
}

export async function verifyForgotPasswordTokenController(req: Request, res: Response) {
  res.json({
    message: USERS_MESSAGES.VERIFY_FORGOT_PASSWORD_TOKEN_SUCCESS,
  });
}

export async function resetPasswordController(req: Request<ParamsDictionary, any, resetPasswordReqBody>, res: Response) {
  const { user_id } = req.decoded_forgot_password_token as TokenPayLoad;
  const { password } = req.body;
  const result = await usersService.resetPassword({ user_id, password });
  res.json(result);
}

export async function emailVerifyTokenController(req: Request<ParamsDictionary, any, VerifyEmailReqBody>, res: Response) {
  const { user_id } = req.decoded_email_verify_token as TokenPayLoad;
  const user = await databaseService.users.findOne({ _id: new ObjectId(user_id) });
  if (user === null) {
    throw new ErrorWithStatus({
      message: USERS_MESSAGES.USER_NOT_FOUND,
      status: 404,
    });
  }
  if (user.verify === UserVerifyStatus.Verified && user.email_verify_token === "") {
    res.json({
      message: USERS_MESSAGES.EMAIL_ALREADY_VERIFIED_BEFORE,
    });
  }
  if (user.email_verify_token !== (req.body.email_verify_token as string)) {
    throw new ErrorWithStatus({
      message: USERS_MESSAGES.EMAIL_VERIFY_TOKEN_IS_INCORRECT,
      status: HTTP_STATUS.UNAUTHORIZED,
    });
  }
  const result = await usersService.verifyEmail(user_id);
  res.json({
