export function verifiedUserValidator(req: Request, res: Response, next: NextFunction) {
  const { verify } = req.decoded_authorization as TokenPayLoad;
  if (verify !== UserVerifyStatus.Verified) {
    return next(
      new ErrorWithStatus({
        message: USERS_MESSAGES.USER_IS_NOT_VERIFIED,
        status: HTTP_STATUS.FORBIDDEN,
      }),
    );
  }
  next();
}

export const changePasswordValidator = validate(
  checkSchema(
    {
      old_password: {
        ...passwordSchema,
        custom: {
          options: async (value, { req }) => {
            const { user_id } = req.decoded_authorization as TokenPayLoad;
            const user = await databaseService.users.findOne({
              _id: new ObjectId(user_id),
            });
            if (user === null) {
              throw new ErrorWithStatus({
                message: USERS_MESSAGES.USER_NOT_FOUND,
                status: HTTP_STATUS.UNAUTHORIZED,
              });
            }
            const isMatch = bcrypt.compareSync(value, user.password);
            if (!isMatch) {
              throw new ErrorWithStatus({
                message: USERS_MESSAGES.OLD_PASSWORD_NOT_MATCH,
                status: HTTP_STATUS.UNAUTHORIZED,
              });
            }
            return true;
          },
        },
      },
      password: { ...passwordSchema, custom: {
        options: (value: string, { req }: Meta) => {
          if (value === req.body.old_password) {
            throw new Error(USERS_MESSAGES.NEW_PASSWORD_MUST_BE_DIFFERENT_FROM_OLD_PASSWORD);
          }
          return true;
        },
      } },
      confirm_password: confirmPasswordSchema,
    },
    ["body"],
  ),
);

export const updateMeValidator = validate(
  checkSchema(
    {
      fullname: {
        optional: true,
        ...fullNameSchema,
        notEmpty: undefined,
      },
      location: {
        optional: true,
        isString: {
          errorMessage: USERS_MESSAGES.LOCATION_MUST_BE_A_STRING,
        },
        trim: true,
        isLength: {
          options: {
            min: 1,
            max: 200,
          },
          errorMessage: USERS_MESSAGES.LOCATION_LENGTH_MUST_BE_LESS_THAN_200,
        },
      },
      username: {
        optional: true,
        isString: {
          errorMessage: USERS_MESSAGES.USERNAME_MUST_BE_A_STRING,
        },
        trim: true,
        custom: {
          options: async (value: string) => {
            if (REGEX_USERNAME.test(value) === false) {
              throw new Error(USERS_MESSAGES.USERNAME_MUST_BE_A_STRING);
            }
            const user = await databaseService.users.findOne({ username: value });

            if (user) {
              throw new Error(USERS_MESSAGES.USERNAME_ALREADY_EXISTS);
            }
            return true;
          },
        },
      },
      avatar: {
        optional: true,
        isString: {
          errorMessage: USERS_MESSAGES.IMAGE_URL_MUST_BE_A_STRING,
        },
        trim: true,
      },
    },
    ["body"],
  ),
);

import { checkSchema } from 'express-validator'
import { ObjectId } from 'mongodb'

import { TransactionTypeEnum, WalletStatus } from '~/constants/enums'
import HTTP_STATUS from '~/constants/http-status'
import { USERS_MESSAGES, WALLETS_MESSAGE } from '~/constants/messages'
import { ErrorWithStatus } from '~/models/errors'
import databaseService from '~/services/database.services'
import { validate } from '~/utils/validation'

export const increaseBalanceValidator = validate(
  checkSchema(
    {
      amount: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.AMOUNT_IS_REQUIRED
        },
        isDecimal: {
          options: { decimal_digits: '1,2' },
          errorMessage: WALLETS_MESSAGE.AMOUNT_NUMERIC
        },
        custom: {
          options: async (value, { req }) => {
            if (parseFloat(value) <= 0) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.AMOUNT_NEGATIVE,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }

            const { user_id } = req.body

            const findUser = await databaseService.users.findOne({ _id: new ObjectId(user_id) })
            if (!findUser) {
              throw new ErrorWithStatus({
                message: USERS_MESSAGES.USER_NOT_FOUND,
                status: HTTP_STATUS.NOT_FOUND
              })
            }

            const findWallet = await databaseService.wallets.findOne({ user_id: new ObjectId(findUser._id) })
            if (!findWallet) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.USER_NOT_HAVE_WALLET.replace('%s', user_id),
                status: HTTP_STATUS.NOT_FOUND
              })
            }

            return true
          }
        }
      },

      fee: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.FEE_IS_REQUIRED
        },
        isDecimal: {
          options: { decimal_digits: '1,2' },
          errorMessage: WALLETS_MESSAGE.FEE_NEGATIVE
        },
        custom: {
          options: (value) => {
            if (parseFloat(value) < 0) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.FEE_NEGATIVE,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }
            return true
          }
        }
      },
      description: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.DESCRIPTION_IS_REQUIED
        },
        isString: {
          errorMessage: WALLETS_MESSAGE.DESCRIPTION_INVALID
        }
      },
      transaction_hash: {
        in: ['body'],
        trim: true,
        optional: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.TRANSACRION_HASH_REQUIRED
        },
        isString: {
          errorMessage: WALLETS_MESSAGE.TRANSACRION_HASH_INVALID
        }
      },
      message: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.MESSAGE_IS_REQUIED
        },
        isString: {
          errorMessage: WALLETS_MESSAGE.MESSAGE_INVALID
        },
        custom: {
          options: async (value, { req }) => {
            const { user_id } = req.body

            const findUser = await databaseService.users.findOne({ _id: new ObjectId(user_id) })
            if (!findUser) {
              throw new ErrorWithStatus({
                message: USERS_MESSAGES.USER_NOT_FOUND,
                status: HTTP_STATUS.NOT_FOUND
              })
            }

            const findWallet = await databaseService.wallets.findOne({ user_id: new ObjectId(findUser._id) })
            if (!findWallet) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.USER_NOT_HAVE_WALLET.replace('%s', user_id),
                status: HTTP_STATUS.NOT_FOUND
              })
            }

            if (findWallet.status === WalletStatus.Frozen) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.WALLET_HAS_BEEN_FROZEN,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }

            return true
          }
        }
      },
      user_id: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.USER_ID_IS_REQUIRED
        },
        isMongoId: {
          errorMessage: WALLETS_MESSAGE.USER_ID_INVALID
        },
        custom: {
          options: async (value) => {
            const findUser = await databaseService.users.findOne({ _id: new ObjectId(value) })
            if (!findUser) {
              throw new ErrorWithStatus({
                message: USERS_MESSAGES.USER_NOT_FOUND,
                status: HTTP_STATUS.NOT_FOUND
              })
            }
            return true
          }
        }
      }
    },
    ['body']
  )
)

export const decreaseBalanceValidator = validate(
  checkSchema(
    {
      amount: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.AMOUNT_IS_REQUIRED
        },
        isDecimal: {
          options: { decimal_digits: '1,2' },
          errorMessage: WALLETS_MESSAGE.AMOUNT_NUMERIC
        },
        custom: {
          options: async (value, { req }) => {
            if (parseFloat(value) <= 0) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.AMOUNT_NEGATIVE,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }

            const { user_id } = req.body

            const findUser = await databaseService.users.findOne({ _id: new ObjectId(user_id) })
            if (!findUser) {
              throw new ErrorWithStatus({
                message: USERS_MESSAGES.USER_NOT_FOUND,
                status: HTTP_STATUS.NOT_FOUND
              })
            }

            const findWallet = await databaseService.wallets.findOne({ user_id: new ObjectId(findUser._id) })
            if (!findWallet) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.USER_NOT_HAVE_WALLET.replace('%s', user_id),
                status: HTTP_STATUS.NOT_FOUND
              })
            }

            if (findWallet.status === WalletStatus.Frozen) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.WALLET_HAS_BEEN_FROZEN,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }

            const amount = Number.parseFloat(value.toString())
            const currentBalance = Number.parseFloat(findWallet.balance.toString())

            if (currentBalance < amount) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.INSUFFICIENT_BALANCE.replace('%s', user_id),
                status: HTTP_STATUS.BAD_REQUEST
              })
            }

            return true
          }
        }
      },
      fee: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.FEE_IS_REQUIRED
        },
        isDecimal: {
          options: { decimal_digits: '1,2' },
          errorMessage: WALLETS_MESSAGE.FEE_NEGATIVE
        },
        custom: {
          options: (value) => {
            if (parseFloat(value) < 0) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.FEE_NEGATIVE,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }
            return true
          }
        }
      },
      description: {
        in: ['body'],
        trim: true,
        optional: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.DESCRIPTION_IS_REQUIED
        },
        isString: {
          errorMessage: WALLETS_MESSAGE.DESCRIPTION_INVALID
        }
      },
      transaction_hash: {
        in: ['body'],
        trim: true,
        optional: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.TRANSACRION_HASH_REQUIRED
        },
        isString: {
          errorMessage: WALLETS_MESSAGE.TRANSACRION_HASH_INVALID
        }
      },
      message: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.MESSAGE_IS_REQUIED
        },
        isString: {
          errorMessage: WALLETS_MESSAGE.MESSAGE_INVALID
        }
      }
    },
    ['body']
  )
)

export const updateWalletStatusValidator = validate(
  checkSchema({
    id: {
      in: ['params'],
      trim: true,
      notEmpty: {
        errorMessage: WALLETS_MESSAGE.WALLET_ID_IS_REQUIED
      },
      isMongoId: {
        errorMessage: WALLETS_MESSAGE.WALLET_ID_INVALID
      },
      custom: {
        options: async (value) => {
          const findWallet = await databaseService.wallets.findOne({ _id: new ObjectId(value) })
          if (!findWallet) {
            throw new ErrorWithStatus({
              message: WALLETS_MESSAGE.WALLET_NOT_FOUND.replace('%s', value),
              status: HTTP_STATUS.NOT_FOUND
            })
          }
        }
      }
    },
    newStatus: {
      in: ['body'],
      trim: true,
      notEmpty: {
        errorMessage: WALLETS_MESSAGE.STATUS_IS_REQUIED
      },
      isIn: {
        options: [[WalletStatus.Active, WalletStatus.Frozen]],
        errorMessage: WALLETS_MESSAGE.STATUS_INVALID
      }
    }
  })
)

import { checkSchema } from 'express-validator'
import { WithDrawalStatus } from '~/constants/enums'
import HTTP_STATUS from '~/constants/http-status'
import { WALLETS_MESSAGE } from '~/constants/messages'
import { ErrorWithStatus } from '~/models/errors'
import { validate } from '~/utils/validation'

export const createWithdrawRequestValidator = validate(
  checkSchema(
    {
      amount: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.AMOUNT_IS_REQUIRED
        },
        isDecimal: {
          options: { decimal_digits: '1,2' },
          errorMessage: WALLETS_MESSAGE.AMOUNT_NUMERIC
        },
        custom: {
          options: (value) => {
            if (parseFloat(value) <= 0) {
              throw new ErrorWithStatus({
                message: WALLETS_MESSAGE.AMOUNT_NEGATIVE,
                status: HTTP_STATUS.BAD_REQUEST
              })
            }
            return true
          }
        }
      },

      account: {
        in: ['body'],
        trim: true,
        notEmpty: {
          errorMessage: WALLETS_MESSAGE.TYPE_IS_REQUIRED
        }
      },

      note: {
        in: ['body'],
        optional: true,
        trim: true,
        isString: {
          errorMessage: WALLETS_MESSAGE.NOTE_IN_VALID
        },
        isLength: {
          options: { max: 500 },
          errorMessage: WALLETS_MESSAGE.NOTE_TOO_LONG
        }
      }
    },
    ['body']
  )
)

export const updateWithdrawStatusValidator = validate(
  checkSchema({
    newStatus: {
      in: ['body'],
      trim: true,
      notEmpty: {
        errorMessage: WALLETS_MESSAGE.STATUS_IS_REQUIED
      },
      isIn: {
        options: [[WithDrawalStatus.Approved, WithDrawalStatus.Completed, WithDrawalStatus.Rejected]],
        errorMessage: WALLETS_MESSAGE.INVALID_NEW_STATUS
      }
    },
    reason: {
      in: ['body'],
      trim: true,
      isString: {
        errorMessage: WALLETS_MESSAGE.REASON_INVALID
      },
      isLength: {
        options: { max: 500 },
        errorMessage: WALLETS_MESSAGE.REASON_TOO_LONG
      },
      custom: {
        options: (value, { req }) => {
          if (req.body.newStatus === WithDrawalStatus.Rejected && (!value || value.trim() === '')) {
            throw new ErrorWithStatus({
              message: WALLETS_MESSAGE.REASON_INVALID,
              status: HTTP_STATUS.BAD_REQUEST
            })
          }

          return true
        }
      }
    }
  })
)

import HTTP_STATUS from "~/constants/http-status";
