import type { NextFunction, Request, RequestHandler, Response } from "express";

export function wrapAsync<P>(func: RequestHandler<P>) {
  return async (req: Request<P>, res: Response, next: NextFunction) => {
    try {
      await func(req, res, next);
    }
    catch (error) {
      next(error);
    }
  };
}

import type { Buffer } from "node:buffer";

import jwt from "jsonwebtoken";

import type { TokenPayLoad } from "~/models/requests/users.requests";

export function signToken({
  payload,
  privateKey,
  options = { algorithm: "HS256" },
}: {
  payload: string | object | Buffer;
  privateKey: string;
  options?: jwt.SignOptions;
}) {
  return new Promise<string>((resolve, reject) => {
    jwt.sign(payload, privateKey, options, (err, token) => {
      if (err)
        throw reject(err);
      resolve(token as string);
    });
  });
}

export function verifyToken({ token, secretOrPublicKey }: { token: string; secretOrPublicKey: string }) {
  return new Promise<TokenPayLoad>((resolve, reject) => {
    jwt.verify(token, secretOrPublicKey, (error, decoded) => {
      if (error)
        throw reject(error);
      resolve(decoded as TokenPayLoad);
    });
  });
}

import type { NextFunction, Request, Response } from "express";
import type { Collection, Document, Filter, Document as Projection } from "mongodb";
import { normalizeDecimal } from "./string";

export type IResponseSearch<T> = {
  data: T[];
  pagination: {
    limit: number;
    currentPage: number;
    totalPages: number;
    totalRecords: number;
  };
};

export async function sendPaginatedResponse<T extends Document>(res: Response, next: NextFunction, collection: Collection<T>, query: Request["query"], filter: Filter<T> = {}, projection: Projection = {}) {
  try {
    const page = Number.parseInt(query.page as string) || 1;
    const limit = Number.parseInt(query.limit as string) || 10;
    const skip = (page - 1) * limit;

    const [totalRecords, data] = await Promise.all([
      collection.countDocuments(filter),
      collection.find(filter, { projection }).sort({ created_at: -1 }).skip(skip).limit(limit).toArray(),
    ]);

    const totalPages = Math.ceil(totalRecords / limit);
    const normalized = data.map(normalizeDecimal)

    const responseBody: IResponseSearch<any> = {
      data: normalized,
      pagination: {
        limit,
        currentPage: page,
        totalPages,
        totalRecords,
      },
    };

    return res.status(200).json(responseBody);
  }
  catch (error) {
    next(error);
  }
}

export function sendPaginatedResponseFromRedis<T>(res: Response, next: NextFunction, data: T[], query: Record<string, any>) {
  try {
    const page = Number.parseInt(query.page as string) || 1;
    const limit = Number.parseInt(query.limit as string) || 10;
    const start = (page - 1) * limit;

    const paginatedData = data.slice(start, start + limit);

    const responseBody: IResponseSearch<any> = {
      data: paginatedData,
      pagination: {
        limit,
        currentPage: page,
        totalPages: Math.ceil(data.length / limit),
        totalRecords: data.length,
      },
    };

    return res.status(200).json(responseBody);
  }
  catch (error) {
    next(error);
  }
}

export async function sendPaginatedAggregationResponse<T extends Document>(
  res: Response,
  next: NextFunction,
  collection: Collection<T>,
  query: Request['query'],
  pipeline: Document[]
) {
  try {
    const page = Number.parseInt(query.page as string) || 1
    const limit = Number.parseInt(query.limit as string) || 10
    const skip = (page - 1) * limit

    const [totalRecordsResult, data] = await Promise.all([
      // Pipeline to count the total matching documents
      collection.aggregate([...pipeline, { $count: 'total' }]).toArray(),
      // Pipeline to get the actual data for the current page
      collection.aggregate([...pipeline, { $skip: skip }, { $limit: limit }]).toArray()
    ])

    const totalRecords = totalRecordsResult.length > 0 ? totalRecordsResult[0].total : 0
    const totalPages = Math.ceil(totalRecords / limit)

    const normalized = data.map(normalizeDecimal)

    const responseBody: IResponseSearch<any> = {
      data: normalized,
      pagination: {
        limit,
        currentPage: page,
        totalPages,
        totalRecords
      }
    }

    return res.status(200).json(responseBody)
  } catch (error) {
    next(error)
  }
}

import { ObjectId } from "mongodb";

export function toObjectId(id: string | ObjectId) {
  return typeof id === "string" ? new ObjectId(id) : id;
}

export function normalizeDecimal(obj: any) {
  if (!obj || typeof obj !== "object") return obj;

  for (const key of Object.keys(obj)) {
    const value = obj[key];
    if (value && value._bsontype === "Decimal128") {
      obj[key] = parseFloat(value.toString());
    } else if (typeof value === "object") {
      normalizeDecimal(value);
    }
  }
  return obj;
}

import type { NextFunction, Request, Response } from "express";
import type { ValidationChain } from "express-validator";
import type { RunnableValidationChains } from "express-validator/lib//middlewares/schema";

import { validationResult } from "express-validator";

import { EntityError, ErrorWithStatus } from "~/models/errors";

export function validate(validation: RunnableValidationChains<ValidationChain>) {
  return async (req: Request, res: Response, next: NextFunction) => {
    await validation.run(req);
    const errors = validationResult(req);
    if (errors.isEmpty()) {
      return next();
    }
    const errorObjects = errors.mapped();
    const entityError = new EntityError({ errors: {} });
    for (const key in errorObjects) {
      const { msg } = errorObjects[key];
      if (msg instanceof ErrorWithStatus && msg.status !== 422) {
        return next(msg);
      }
      entityError.errors[key] = { msg: msg instanceof Error ? msg.message : msg };
    }
    next(entityError);
  };
}

