"use client";

import type React from "react";

import { useEffect, useState } from "react";
import { DashboardLayout } from "@/components/dashboard/dashboard-layout";
import type { DetailUser } from "@/services/authService";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Camera, Save, X, Mail } from "lucide-react";
import { cn } from "@/lib/utils";
import { useAuth } from "@/providers/auth-providers";
import { Progress } from "@radix-ui/react-progress";
import { useAuthActions } from "@/hooks/useAuthAction";
import Image from "next/image";
import { UpdateProfileSchemaFormData } from "@/schemas/authSchema";
import { clearTokens } from "@/utils/tokenManager";
import Link from "next/link";
export default function ProfilePage() {
  const { user, updateProfile  } = useAuth();
  const [isEditing, setIsEditing] = useState<boolean>(false);
  const [data,setData] = useState<DetailUser | null>(null);
  const [formData, setFormData] = useState<DetailUser>(() => user || {} as DetailUser);
  const [avatarPreview, setAvatarPreview] = useState(user?.avatar ?? "");
  const { resendVerifyEmail } = useAuthActions();
  useEffect(() => {
    if (user) {
      setData(user);
      setFormData(user as DetailUser);
      setAvatarPreview(user.avatar ?? "");
      console.log(user);
    }
  }, [user]);
  if (!user || !data) {
    return <div><Progress /></div>;
  }
  const handleInputChange = (field: keyof DetailUser, value: string) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };
  const handleAvatarChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatarPreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };  
  const handleSave = () => {
    if (!user) return;
    const fields: (keyof UpdateProfileSchemaFormData)[] = [
      "fullname",
      "username",
      "phone_number",
      "location",
      "avatar",
    ];

    const updatedData = fields.reduce((acc, field) => {
      const newValue = field === "avatar" ? avatarPreview : formData[field];
      const oldValue = user[field as keyof DetailUser] ?? "";

      if (newValue !== oldValue) {
        acc[field] = newValue || "";
      }
      return acc;
    }, {} as UpdateProfileSchemaFormData);

    // Nếu có field nào thay đổi mới gọi API
    if (Object.keys(updatedData).length > 0) {
      updateProfile(updatedData);
    }

    setIsEditing(false);
  };

  const handleCancel = () => {
    if (data) {
      setFormData(data as DetailUser);
      setAvatarPreview(data.avatar || "");
    }
    setIsEditing(false);
  };
  const handleResendVerifyEmail = () => {
    if (formData?.verify === "VERIFIED") {
      return;
    }
    resendVerifyEmail();
  };
  if (user.role !== "STAFF") {
    clearTokens();
    window.location.href = "/auth/login";
    return null;
  }
    return (
      <DashboardLayout user={data}>
        <div className="space-y-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold text-foreground">
                Hồ sơ cá nhân
              </h1>
              <p className="text-muted-foreground mt-1">
                Quản lý thông tin tài khoản của bạn
              </p>
            </div>
            {!isEditing ? (
              <Button
                onClick={() => setIsEditing(true)}
                className="bg-primary hover:bg-primary/90"
              >
                Chỉnh sửa hồ sơ
              </Button>
            ) : (
              <div className="flex gap-2">
                <Button
                  onClick={handleCancel}
                  variant="outline"
                  className="gap-2 bg-transparent"
                >
                  <X className="w-4 h-4" />
                  Hủy
                </Button>
                <Button
                  onClick={() => handleSave()}
                  className="bg-primary hover:bg-primary/90 gap-2"
                >
                  <Save className="w-4 h-4" />
                  Lưu thay đổi
                </Button>
              </div>
            )}
          </div>

          <div className="bg-card border border-border rounded-xl p-8">
            <div className="flex flex-col md:flex-row gap-8">
              <div className="flex flex-col items-center gap-4">
                <div className="relative group">
                  <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-primary/20">
                    <Image
                      src={avatarPreview || "/placeholder.svg"}
                      alt="Avatar"
                      className="w-full h-full object-cover"
                      width={128}
                      height={128}
                    />
                  </div>
                  {isEditing && (
                    <label
                      htmlFor="avatar-upload"
                      className="absolute inset-0 flex items-center justify-center bg-black/50 rounded-full opacity-0 group-hover:opacity-100 transition-opacity cursor-pointer"
                    >
                      <Camera className="w-8 h-8 text-white" />
                      <input
                        id="avatar-upload"
                        type="file"
                        accept="image/*"
                        className="hidden"
                        onChange={handleAvatarChange}
                      />
                    </label>
                  )}
                </div>
                <div className="text-center">
                  <p className="text-sm font-medium text-foreground">
                    {formData?.fullname || "Chưa có tên"}
                  </p>
                  <p
                    className={cn(
                      "text-xs px-2 py-1 rounded-full inline-block mt-1",
                      formData?.role === "ADMIN"
                        ? "bg-primary/20 text-primary"
                        : "bg-accent/20 text-accent-foreground"
                    )}
                  >
                    {formData?.role === "ADMIN" ? "Quản trị viên" : "Nhân viên"}
                  </p>
