export const useBikeActions = (
  hasToken : boolean, 
  bikeId?: string
) => {
    const router = useRouter();
    const useGetBikes = useGetAllBikeQuery();
    const useCreateBike = useCreateBikeMutation();
    const useGetStatusBike = useGetStatusBikeQuery();
    const useGetStatusBikeByID = useGetStatusBikeIDQuery(bikeId || '');
    const updateBikeMutation = useUpdateBike();
    const deleteBikeMutation = useSoftDeleteBikeMutation();
    const reportBikeMutation = useReportBike();
    const useGetDetailBike = useGetBikeByIDAllQuery(bikeId || '');
    const queryClient = useQueryClient();
    const getBikes = useCallback(() => {
        if(!hasToken){
            router.push('/login');
            return;
        };
        useGetBikes.refetch();
    }, [useGetBikes, hasToken, router]);
    const createBike = useCallback(
      (data: BikeSchemaFormData) => {
        if (!hasToken) {
          router.push("/login");
          return;
        }
        useCreateBike.mutate(data, {
          onSuccess: (result) => {
            if (result.status === 201) {
              toast.success("Bike created successfully");
            } else {
              const errorMessage =
                result.data?.message || "Error creating bikes";
              toast.error(errorMessage);
            }
          },
          onError: (error) => {
            const errorMessage = getErrorMessage(error, "Error creating bikes");
            toast.error(errorMessage);
          },
        });
      },
      [useCreateBike, hasToken, router]
    );
    const updateBike = useCallback(
      (data: UpdateBikeSchemaFormData, id: string) => {
        if (!hasToken) {
          router.push("/login");
          return;
        }
        updateBikeMutation.mutate(
          { id, data },
          {
            onSuccess: (result) => {
              if (result.status === 200) {
                toast.success("Bike updated successfully");
              } else {
                const errorMessage =
                  result.data?.message || "Error updating bikes";
                toast.error(errorMessage);
              }
            },
            onError: (error) => {
              const errorMessage = getErrorMessage(
                error,
                "Error updating bikes"
              );
              toast.error(errorMessage);
            },
          }
        );
      },
      [updateBikeMutation, hasToken, router]
    );
    const deleteBike = useCallback(
      (id: string) => {
        if (!hasToken) {
          router.push("/login");
          return;
        }
        deleteBikeMutation.mutate(id, {
          onSuccess: (result) => {
            if (result.status === 200) {
              toast.success("Bike deleted successfully");
              queryClient.invalidateQueries({ queryKey: ["bikes"] });
            } else {
              const errorMessage =
                result.data?.message || "Error deleting bikes";
              toast.error(errorMessage);
            }
          },
          onError: (error) => {
            const errorMessage = getErrorMessage(error, "Error deleting bikes");
            toast.error(errorMessage);
          },
        });
      },
      [deleteBikeMutation, hasToken, router, queryClient]
    );
    const reportBike = useCallback(
    (id: string) => {
      if (!hasToken) {
        router.push("/login");
        return;
      }
      reportBikeMutation.mutate(id, {
        onSuccess: (result) => {
          if (result.status === 200) {
            toast.success("Bike reported successfully");
          } else {
            const errorMessage = result.data?.message || "Error reporting bike";
            toast.error(errorMessage);
          }
        },
        onError: (error) => {
          const errorMessage = getErrorMessage(error, "Error reporting bike");
          toast.error(errorMessage);
        },
      });
    },
    [reportBikeMutation, hasToken, router]
    );
    const getBikeByID = useCallback(() => {
      useGetDetailBike.refetch();
    }, [useGetDetailBike]);
    const getStatusBike = useCallback(() => {
      useGetStatusBike.refetch();
    }, [useGetStatusBike]);
    const getStatusBikeByID = useCallback(() => {
      useGetStatusBikeByID.refetch();
    } , [useGetStatusBikeByID]);
    return {
      getBikes,
      createBike,
      updateBike,
      deleteBike,
      reportBike,
      getBikeByID,
      getStatusBike,
      getStatusBikeByID,
      isFetchingStatusBikeByID: useGetStatusBikeByID.isFetching,
      isFetchingStatusBike: useGetStatusBike.isFetching,
      isFetchingBikeDetail: useGetBikes.isFetching,
      isFetchingBike: useGetDetailBike.isFetching,
      isReportingBike: reportBikeMutation.isPending,
      isDeletingBike: deleteBikeMutation.isPending,
      isGettingBikes: useGetBikes.isFetching,
      isUpdatingBike: updateBikeMutation.isPending,
      isCreatingBike: useCreateBike.isPending,
    };
}

import axios, { AxiosInstance, AxiosRequestConfig, AxiosResponse } from "axios";
import {clearTokens,getAccessToken,getRefreshToken,setTokens} from "@utils/tokenManager"
export const HTTP_STATUS = {
  OK: 200,
  UNAUTHORIZED: 401,
  FORBIDDEN: 403,
  NOT_FOUND: 404,
  INTERNAL_SERVER_ERROR: 500,
  SERVICE_UNAVAILABLE: 503,
};

export class FetchHttpClient {
  private baseURL: string;
  private axiosInstance: AxiosInstance;
  private isRefreshing = false;
  private failedQueue: Array<{
    resolve: (value: string | null) => void;
    reject: (reason?: unknown) => void;
  }> = [];

  constructor(baseURL: string) {
    this.baseURL = baseURL;
    this.axiosInstance = axios.create({
      baseURL: this.baseURL,
      headers: {
        "Content-Type": "application/json;charset=UTF-8",
      },
       timeout: 30000, // Tăng lên 30 giây để đủ thời gian cho email service
    });
    this.axiosInstance.interceptors.request.use((config) => {
      const access_token = getAccessToken();
      if (access_token && !config.headers?.Authorization) {
        config.headers.Authorization = `Bearer ${access_token}`;
      }
      return config;
    });

    this.axiosInstance.interceptors.response.use(
      (response) => {
        console.log('API Response:', response.status, response.config.url);
        return response;
      },
      async (error) => {
        console.log('API Error:', error.response?.status, error.config?.url, error.response?.data);
        const originalRequest = error.config;
        if (error.response?.status === HTTP_STATUS.UNAUTHORIZED) {
         window.dispatchEvent(new Event("auth:session_expired"));
          if (this.isRefreshing) {
            return new Promise((resolve, reject) => {
              this.failedQueue.push({ resolve, reject });
            })
              .then((token) => {
                if (token && originalRequest.headers) {
                  originalRequest.headers.Authorization = `Bearer ${token}`;
                }
                return this.axiosInstance(originalRequest);
              })
              .catch((err) => {
                return Promise.reject(err);
              });
          }

          this.isRefreshing = true;
          try {
            const newToken = await this.refreshAccessToken();
            this.processQueue(null, newToken);
            window.dispatchEvent(new Event("auth:token_refreshed"));
            if (originalRequest.headers) {
              originalRequest.headers.Authorization = `Bearer ${newToken}`;
            }
            return this.axiosInstance(originalRequest);
          } catch (refreshError) {
            this.processQueue(refreshError, null);
            return Promise.reject(refreshError);
          } finally {
            this.isRefreshing = false;
          }
        }
        // switch (error.response?.status) {
        //   case HTTP_STATUS.FORBIDDEN:
        //     console.log("API: 403 Forbidden");
        //     window.location.href = `/error/${HTTP_STATUS.FORBIDDEN}`;
        //     break;
        //   case HTTP_STATUS.NOT_FOUND:
        //     console.log("API: 404 Not Found");
        //     break;
        //   // case HTTP_STATUS.INTERNAL_SERVER_ERROR:
        //   //   console.log("API: 500 Internal Server Error");
        //   //   window.location.href = `/error/${HTTP_STATUS.INTERNAL_SERVER_ERROR}`;
        //   //   break;
        //   case HTTP_STATUS.SERVICE_UNAVAILABLE:
        //     console.log("API: 503 Service Unavailable");
        //     window.location.href = `/error/${HTTP_STATUS.SERVICE_UNAVAILABLE}`;
        //     break;
        //   default:
        //     console.error(`API Error: ${error.response?.status}`);
        // }

        return Promise.reject(error);
      }
    );
  }

  private async refreshAccessToken(): Promise<string> {
    const refreshToken = getRefreshToken();
    console.log('Refreshing token with:', refreshToken);
    if (!refreshToken) {
      throw new Error("No refresh token available");
    }
    const response = await axios.post(
      `${this.baseURL}/users/refresh-token`,
      { refresh_token: refreshToken },
      { headers: { "Content-Type": "application/json" } }
    );
    console.log('Refresh token response:', response.status, response.data);
    if (response.status !== HTTP_STATUS.OK) {
      clearTokens();
      window.location.href = "/login";
      throw new Error("Refresh token expired");
    }
    const data = response.data;
    setTokens(data.result.access_token, data.result.refresh_token);
    return data.result.access_token;
  }

  private processQueue(error: unknown, token: string | null) {
    this.failedQueue.forEach(({ resolve, reject }) => {
      if (error) {
        reject(error);
      } else {
        resolve(token);
      }
    });
    this.failedQueue = [];
  }

   async get<T>(url: string, params?: AxiosRequestConfig["params"]): Promise<AxiosResponse<T>> {
    return this.axiosInstance.get(url, {
      params: params ?? {},
    });
  }

   async post<T>(
    url: string,
    data?: AxiosRequestConfig["data"],
    config?: AxiosRequestConfig<unknown> | undefined
  ): Promise<AxiosResponse<T>> {
    return this.axiosInstance.post(url, data, config);
  }
  //axios.put(url[, data[, config]])
  async put<T>(
    url: string,
    data?: AxiosRequestConfig["data"],
    config?: AxiosRequestConfig<unknown> | undefined
  ): Promise<AxiosResponse<T>> {
    return this.axiosInstance.put(url, data, config);
  }
  //axios.patch(url[, data[, config]])
  async patch<T>(
    url: string,
    data?: AxiosRequestConfig["data"],
    config?: AxiosRequestConfig<unknown> | undefined
  ): Promise<AxiosResponse<T>> {
    return this.axiosInstance.patch(url, data, config);
  }
  //axios.delete(url[, config])
  async delete<T>(url: string, params?: AxiosRequestConfig["params"]): Promise<AxiosResponse<T>> {
    return this.axiosInstance.delete(url, {
      params,
    });
  }
}

