import { BusinessLogicError } from "../../middleware";

type CommandAcceptedResponse = IotService.CommandAcceptedResponse;

const { validateStateTransition, ERROR_CODES } = IotService;
const { normalizeMac, topicWithMac, IOT_COMMAND_TOPICS } = IotTopics;

type Dependencies = {
  commandPublisher: CommandPublisher;
  deviceManager: DeviceManager;
};

type StateCommandValue = IotTopics.IotStateCommand;
type BookingCommandValue = IotTopics.IotBookingCommand;
type ReservationCommandValue = IotTopics.IotReservationCommand;
type MaintenanceCommandValue = IotTopics.IotMaintenanceCommand;
type StatusCommandValue = IotTopics.IotStatusCommand;

type StateCommandInput = {
  deviceId: string;
  state: StateCommandValue;
};

type BookingCommandInput = {
  deviceId: string;
  command: BookingCommandValue;
};

type ReservationCommandInput = {
  deviceId: string;
  command: ReservationCommandValue;
};

type MaintenanceCommandInput = {
  deviceId: string;
  command: MaintenanceCommandValue;
};

type StatusCommandInput = {
  deviceId: string;
  command?: StatusCommandValue;
};

export class CommandController {
  constructor(private readonly deps: Dependencies) {}

  async sendStateCommand(input: StateCommandInput): Promise<CommandAcceptedResponse> {
    const normalizedId = this.normalizeDeviceId(input.deviceId);

    const currentState = this.deps.deviceManager.getDeviceState(normalizedId);
    if (currentState) {
      if (currentState === input.state) {
        this.throwDeviceAlreadyInState(normalizedId, currentState, input.state);
      }

      this.ensureValidTransition(currentState, input.state);
    }

    await this.deps.commandPublisher.sendStateCommand(input.state, normalizedId);

    return this.createAcceptedResponse(
      normalizedId,
      topicWithMac(IOT_COMMAND_TOPICS.state, normalizedId),
      input.state,
    );
  }

  async sendBookingCommand(input: BookingCommandInput): Promise<CommandAcceptedResponse> {
    const normalizedId = this.normalizeDeviceId(input.deviceId);
    const currentState = this.getExistingDeviceState(normalizedId);

    const targetState: StateCommandValue = input.command === "release" ? "available" : "booked";

    if (currentState === targetState) {
      this.throwDeviceAlreadyInState(normalizedId, currentState, targetState);
    }

    this.ensureValidTransition(currentState, targetState);

    await this.deps.commandPublisher.sendBookingCommand(input.command, normalizedId);

    return this.createAcceptedResponse(
      normalizedId,
      topicWithMac(IOT_COMMAND_TOPICS.booking, normalizedId),
      input.command,
    );
  }

  async sendReservationCommand(input: ReservationCommandInput): Promise<CommandAcceptedResponse> {
    const normalizedId = this.normalizeDeviceId(input.deviceId);
    const currentState = this.getExistingDeviceState(normalizedId);

    const targetState: StateCommandValue = input.command === "reserve" ? "reserved" : "available";

    if (currentState === targetState) {
      this.throwDeviceAlreadyInState(normalizedId, currentState, targetState);
    }

    this.ensureValidTransition(currentState, targetState);

    await this.deps.commandPublisher.sendReservationCommand(input.command, normalizedId);

    return this.createAcceptedResponse(
      normalizedId,
      topicWithMac(IOT_COMMAND_TOPICS.reservation, normalizedId),
      input.command,
    );
  }

  async sendMaintenanceCommand(input: MaintenanceCommandInput): Promise<CommandAcceptedResponse> {
    const normalizedId = this.normalizeDeviceId(input.deviceId);
    const currentState = this.getExistingDeviceState(normalizedId);

    const targetState: StateCommandValue = input.command === "start" ? "maintained" : "available";

    if (currentState === targetState) {
      this.throwDeviceAlreadyInState(normalizedId, currentState, targetState);
    }

    this.ensureValidTransition(currentState, targetState);

    await this.deps.commandPublisher.sendMaintenanceCommand(input.command, normalizedId);

    return this.createAcceptedResponse(
      normalizedId,
      topicWithMac(IOT_COMMAND_TOPICS.maintenance, normalizedId),
      input.command,
    );
  }

  async requestStatusCommand(input: StatusCommandInput): Promise<CommandAcceptedResponse> {
    const normalizedId = this.normalizeDeviceId(input.deviceId);
    const command: StatusCommandValue = input.command ?? "request";

    await this.deps.commandPublisher.requestStatus(normalizedId);

    return this.createAcceptedResponse(
      normalizedId,
      topicWithMac(IOT_COMMAND_TOPICS.status, normalizedId),
      command,
    );
  }

  private normalizeDeviceId(deviceId: string): string {
    const normalized = normalizeMac(deviceId);
    if (!normalized) {
      throw new BusinessLogicError(
        "Invalid device identifier",
        {
          details: {
            code: "INVALID_DEVICE_IDENTIFIER",
            deviceId,
          },
          status: 400,
        },
      );
    }
    return normalized;
  }

  private getExistingDeviceState(deviceId: string): string {
    const state = this.deps.deviceManager.getDeviceState(deviceId);
    if (!state) {
      throw new BusinessLogicError(
        `Device ${deviceId} not found`,
        {
          details: {
            code: ERROR_CODES.DEVICE_NOT_FOUND,
            deviceId,
          },
          status: 400,
        },
      );
    }
    return state;
  }

  private ensureValidTransition(currentState: string, requestedState: string) {
    const validation = validateStateTransition(currentState, requestedState);
    if (!validation.valid) {
      throw new BusinessLogicError(
        validation.error.message,
        {
          details: {
            code: validation.error.code,
            ...validation.error.details,
          },
          status: 409,
        },
      );
    }
  }

  private throwDeviceAlreadyInState(
    deviceId: string,
    currentState: string,
    requestedState: string,
  ): never {
    throw new BusinessLogicError(
      `Device ${deviceId} is already in state ${currentState}`,
      {
        details: {
          code: ERROR_CODES.DEVICE_ALREADY_IN_STATE,
          deviceId,
          currentState,
          requestedState,
        },
        status: 409,
      },
    );
  }

  private createAcceptedResponse(
    deviceId: string,
    topic: string,
    payload: string,
  ): CommandAcceptedResponse {
    return {
      deviceId,
      topic,
      payload,
    };
  }
}

import type { OpenAPIHono } from "@hono/zod-openapi";
import type {
  CommandAcceptedResponse,
  ErrorResponse,
} from "@mebike/shared";
import type { Context } from "hono";

import { iotServiceRoutes } from "@mebike/shared";

import type { CommandPublisher } from "../../publishers";
import type { DeviceManager } from "../../services";

import { BusinessLogicError } from "../../middleware";
import { CommandController } from "../controllers/command-controller";

export type RegisterCommandRoutesOptions = {
  commandPublisher: CommandPublisher;
  deviceManager: DeviceManager;
};

export function registerCommandRoutes(
  app: OpenAPIHono,
  { commandPublisher, deviceManager }: RegisterCommandRoutesOptions,
): void {
  const controller = new CommandController({ commandPublisher, deviceManager });

  const handleCommandResponse = async (
    c: Context,
    handler: () => Promise<CommandAcceptedResponse>,
  ) => {
    try {
      const result = await handler();
      return c.json<CommandAcceptedResponse, 202>(result, 202);
    }
    catch (err) {
      if (err instanceof BusinessLogicError) {
        const details = err.details;
        const status: 400 | 409 = err.status === 409 ? 409 : 400;
        return c.json<ErrorResponse, typeof status>(
          {
            error: err.message,
            ...(details ? { details } : {}),
          },
          status,
        );
      }
      throw err;
    }
  };

  app.openapi(iotServiceRoutes.sendStateCommand, async (c) => {
    const { deviceId } = c.req.valid("param");
    const { state } = c.req.valid("json");
    return handleCommandResponse(c, () => controller.sendStateCommand({ deviceId, state }));
  });

  app.openapi(iotServiceRoutes.sendBookingCommand, async (c) => {
    const { deviceId } = c.req.valid("param");
    const { command } = c.req.valid("json");
    return handleCommandResponse(c, () => controller.sendBookingCommand({ deviceId, command }));
  });

  app.openapi(iotServiceRoutes.sendReservationCommand, async (c) => {
    const { deviceId } = c.req.valid("param");
    const { command } = c.req.valid("json");
    return handleCommandResponse(c, () => controller.sendReservationCommand({ deviceId, command }));
  });

  app.openapi(iotServiceRoutes.sendMaintenanceCommand, async (c) => {
    const { deviceId } = c.req.valid("param");
    const { command } = c.req.valid("json");
    return handleCommandResponse(c, () => controller.sendMaintenanceCommand({ deviceId, command }));
  });

  app.openapi(iotServiceRoutes.requestStatusCommand, async (c) => {
    const { deviceId } = c.req.valid("param");
    const body = c.req.valid("json");
    return handleCommandResponse(c, () => controller.requestStatusCommand({
      deviceId,
      command: body?.command,
    }));
  });
}

import type { OpenAPIHono } from "@hono/zod-openapi";
import type { DeviceStatus, ErrorResponse } from "@mebike/shared";

import {
  iotServiceRoutes,
  normalizeMac,
} from "@mebike/shared";
import process from "node:process";

import type { DeviceManager } from "../../services";

export type RegisterDeviceRoutesOptions = {
  deviceManager: DeviceManager;
};

export function registerDeviceRoutes(
  app: OpenAPIHono,
  { deviceManager }: RegisterDeviceRoutesOptions,
): void {
  app.openapi(iotServiceRoutes.health, (c) => {
    return c.json({
      status: "ok",
      uptimeMs: Math.round(process.uptime() * 1000),
      timestamp: new Date().toISOString(),
    });
  });

  app.openapi(iotServiceRoutes.listDevices, (c) => {
    const items = Array.from(deviceManager.getAllDeviceStates().entries()).map(([
      deviceId,
      status,
    ]) => ({ deviceId, status }));

    return c.json({ items }, 200);
  });

  app.openapi(iotServiceRoutes.getDevice, (c) => {
    const { deviceId } = c.req.valid("param");
    const normalized = normalizeMac(deviceId);

    if (!normalized) {
      return c.json<ErrorResponse, 400>({
        error: "Invalid device identifier",
        details: { deviceId },
      }, 400);
    }

    const status = deviceManager.getDeviceState(normalized);
    if (!status) {
      return c.json<ErrorResponse, 404>({
        error: "Device not found",
        details: { deviceId: normalized },
      }, 404);
    }

    return c.json<DeviceStatus, 200>({ deviceId: normalized, status }, 200);
  });
}

import type { OpenAPIHono } from "@hono/zod-openapi";
import type { AddressInfo } from "node:net";

import { serve } from "@hono/node-server";

export type HttpServer = ReturnType<typeof serve>;

export type StartHttpServerOptions = {
  port: number;
  hostname?: string;
  logger?: Pick<typeof console, "info" | "error">;
};

export function startHttpServer(
  app: OpenAPIHono,
  { port, hostname, logger = console }: StartHttpServerOptions,
): HttpServer {
  const server = serve(
    {
      fetch: app.fetch,
      port,
      hostname,
    },
    (info: AddressInfo) => {
      const resolvedHost = hostname ?? normalizeListeningAddress(info.address);
      logger.info?.(
        `HTTP server listening on http://${resolvedHost}:${info.port}`,
      );
    },
  );

  server.on("error", (error) => {
    logger.error?.("HTTP server error:", error);
  });

  return server;
}

function normalizeListeningAddress(address: string): string {
  if (address === "::") {
    return "localhost";
  }

  return address;
}

import process from "node:process";

import { bootstrap } from "./bootstrap";
import { env } from "./config";

async function main() {
  try {
    const app = await bootstrap();

    const config = {
      mqtt: {
        brokerUrl: env.MQTT_URL,
        username: env.MQTT_USERNAME,
        password: env.MQTT_PASSWORD,
      },
      http: {
        port: env.HTTP_PORT,
        hostname: env.HTTP_HOST,
      },
      deviceMac: env.DEVICE_MAC,
      stateMachine: {
        stepDelayMs: Number.parseInt(env.STATE_STEP_DELAY_MS, 10),
        transitionTimeoutMs: Number.parseInt(env.STATE_TIMEOUT_MS, 10),
      },
    };

    await app.start(config);

    const shutdown = async () => {
      console.warn("Received shutdown signal, stopping application...");
      await app.stop();
      process.exit(0);
    };

    process.on("SIGINT", shutdown);
    process.on("SIGTERM", shutdown);
  }
  catch (error) {
    console.error("Application failed to start:", error);
    process.exit(1);
  }
}

