bool initializeNetwork(const char *ssid, const char *password, NetworkTopics &topics)
{
    Log.info("Connecting to WiFi...");
    WiFi.begin(ssid, password);

    const int maxAttempts = 20;
    int attempts = 0;

    while (WiFi.status() != WL_CONNECTED && attempts < maxAttempts)
    {
        delay(500);
        attempts++;
        if (attempts % 4 == 0) // 4000 % 4 = 0 so log every 2 seconds == every 4 attempts
        {
            Log.info("WiFi connection attempt %d/%d...\n", attempts, maxAttempts);
        }
    }

    if (WiFi.status() == WL_CONNECTED)
    {
        Log.info("Connected to WiFi! IP: %s\n", WiFi.localIP().toString().c_str());
        topics.statusTopic = makeTopicWithMac("esp/status");
        topics.logTopic = makeTopicWithMac("esp/logs");
        topics.commandStateTopic = makeTopicWithMac("esp/commands/state");
        topics.commandBookingTopic = makeTopicWithMac("esp/commands/booking");
        topics.commandReservationTopic = makeTopicWithMac("esp/commands/reservation");
        topics.commandMaintenanceTopic = makeTopicWithMac("esp/commands/maintenance");
        topics.commandStatusTopic = makeTopicWithMac("esp/commands/status");
        topics.commandRootTopic = makeTopicWithMac("esp/commands");
        topics.maintenanceStatusTopic = makeTopicWithMac("esp/maintenance/status");
        Log.info("Topics set with MAC address\n");
        return true;
    }
    else
    {
        Log.error("WiFi connection failed after %d attempts. Check credentials and network.\n", maxAttempts);
        return false;
    }
}

// Các quy tắc chuyển đổi trạng thái
//
// Nếu xe đang ở trạng thái RESERVED, nó chỉ có thể trở thành:
// - AVAILABLE (nếu việc đặt trước bị hủy).
// - BOOKED (nếu người dùng nhận xe).
// Mọi chuyển đổi khác đều bị từ chối.
//
// Nếu xe đang ở trạng thái BOOKED (đang được sử dụng), nó chỉ có thể trở thành:
// - AVAILABLE (khi người dùng trả xe).
// - BROKEN, MAINTAINED, hoặc UNAVAILABLE (nếu có sự cố được báo cáo hoặc phát hiện).
// Nó không thể được đặt trước hoặc thuê lại cho đến khi được trả lại.
//
// Nếu xe đang ở trạng thái BROKEN, nó chỉ có thể được chuyển sang:
// - MAINTAINED hoặc UNAVAILABLE.
// Nó không thể trở thành AVAILABLE hoặc được khách hàng sử dụng cho đến khi đã được bảo trì.
//
// Nếu xe đang trong trạng thái MAINTAINED, nó chỉ có thể trở thành:
// - AVAILABLE (sau khi bảo trì hoàn tất).
// - UNAVAILABLE.
// Nó không thể được thuê, đặt trước, hoặc báo hỏng trực tiếp từ trạng thái này.
//
// Nếu xe đang ở trạng thái AVAILABLE, nó có thể chuyển sang hầu hết các trạng thái hoạt động khác (RESERVED, BOOKED, BROKEN, v.v.).

#include "CommandHandler.h"
